    <td>&nbsp;</td>
    <td><button type="button" id="sendContactsForm" class="rpwp-button compare-box-button colorize-2"><span class="gloss"></span>Send Message</button>
    <input type="hidden" name="do_action" id="do_action" value="">
    </td>
  </tr>
  </table>
</form>
<script language="javascript">
jQuery(document).ready(function($) {
	var emailRegex = new RegExp(/^([\w\.\-]+)@([\w\-]+)((\.(\w){2,5})+)$/i);
	$(function(){
		$('#sendContactsForm').click(function() {
			var fields = ['fullname', 'email', 'subject', 'comment', 'agreed'];

			fields.forEach(function(e){
				var el = $('#contactsForm #' + e);
				var val = el.val();

				if ((el.is(':not(:checkbox)') && val.length < 3) || (e == 'email' && !emailRegex.test(val)) || (el.is(':checkbox') && el.prop('checked') == false)) {
					el.addClass('error');
				} else {
					el.removeClass('error');
				}
			});

			if (!$('#contactsForm input').hasClass('error')) {
				$('#do_action').attr('value','s'+'end');
				$('#contactsForm').submit();
			}
		});
	})
});
</script>
<?php }?>